mapper.select:
SELECT b.blog_id, b.title, b.description, t.type_name, b.create_time FROM blog b, favorites f, type t WHERE ${uid} = f.uid AND f.blog_id = b.blog_id AND t.type_id = b.type_id
AND b.title LIKE '%${queryPageBean.queryString}%'
ORDER BY f.create_time DESC LIMIT ${queryPageBean.pageSize * (queryPageBean.currentPage-1)},#{queryPageBean.pageSize}

SELECT b.blog_id, u.nickname, u.avatar, t.type_name, b.views, b.description, b.create_time ,b.recommend, b.published, b.update_time, b.title, b.published, b.first_picture, b.thumbs FROM blog b, user u, type t WHERE b.uid = u.uid AND b.type_id = t.type_id
AND b.content LIKE '%${queryPageBean.queryString}%'
AND b.type_id = #{queryPageBean.typeId}
ORDER BY b.views DESC LIMIT ${queryPageBean.pageSize * (queryPageBean.currentPage-1)},#{queryPageBean.pageSize}
